require('dotenv').config();
const alchemyKey = process.env.REACT_APP_ALCHEMY_KEY;
const { createAlchemyWeb3 } = require("@alch/alchemy-web3");
const web3 = createAlchemyWeb3(alchemyKey); 

const contractABI = require("../contract-abi.json");
const contractAddress = "0x928D2Dc6E18b72EcCFfa3Eb48d56c8D66C86db74";

export const lotteryContract = new web3.eth.Contract(
    contractABI.abi,
    contractAddress
);

export const loadCurrentLottery = async () => { 
    const lottery = await lotteryContract.methods.Lottery().call();
    return lottery;
};

export const connectWallet = async () => {
    if (window.ethereum) {
        try {
        const addressArray = await window.ethereum.request({
            method: "eth_requestAccounts",
        });
        const obj = {
            address: addressArray[0],
        };
        return obj;
        } catch (err) {
        return {
            address: "",
            status: "ğŸ˜¥ " + err.message,
        };
        }
    } else {
        return {
        address: "",
        status: (
            <span>
            <p>
                {" "}
                ğŸ¦Š{" "}
                <a target="_blank" href={`https://metamask.io/download`}>
                You must install Metamask, a virtual Ethereum wallet, in your
                browser.
                </a>
            </p>
            </span>
        ),
        };
    }
};

export const getCurrentWalletConnected = async () => {
    if (window.ethereum) {
        try {
        const addressArray = await window.ethereum.request({
            method: "eth_accounts",
        });
        if (addressArray.length > 0) {
            return {
            address: addressArray[0],
            };
        } else {
            return {
            address: "",
            status: "ğŸ¦Š Connect to Metamask using the top right button.",
            };
        }
        } catch (err) {
        return {
            address: "",
            status: "ğŸ˜¥ " + err.message,
        };
        }
    } else {
        return {
        address: "",
        status: (
            <span>
            <p>
                {" "}
                ğŸ¦Š{" "}
                <a target="_blank" href={`https://metamask.io/download`}>
                You must install Metamask, a virtual Ethereum wallet, in your
                browser.
                </a>
            </p>
            </span>
        ),
        };
    }
};


//set up transaction parameters
const transactionParameters = {
    to: contractAddress, // Required except during contract publications.
    from: address, // must match user's active address.
    data: helloWorldContract.methods.update(message).encodeABI(),
};

export const endLottery = async (address, message) => {
    sendTransaction(address, message, lotteryContract.methods.endLottery().encodeABI());
}

export const startLottery = async (address, message, amountOfDays, ticketPrice) => {
    sendTransaction(address, message, lotteryContract.methods.startLottery(amountOfDays, ticketPrice).encodeABI());
}

export const buyTicket = async (address, message) => {
    sendTransaction(address, message, lotteryContract.methods.buyTicket().encodeABI());
}

export const changeTicketPrice = async (address, message, ticketPrice) => {
    sendTransaction(address, message, lotteryContract.methods.changeTicketPrice().encodeABI());
}

export const cancelLottery = async (address, message) => {
    sendTransaction(address, message, lotteryContract.methods.cancelLottery().encodeABI());
}

export const retrySubmissionOfFounds = async (address, message) => {
    sendTransaction(address, message, lotteryContract.methods.retrySubmissionOfFounds().encodeABI());
}

export const sendTransaction = async (address, message, dataMethod) => {

    //input error handling
    if (!window.ethereum || address === null) {
      return {
        status:
          "ğŸ’¡ Connect your Metamask wallet to update the message on the blockchain.",
      };
    }

    if (message.trim() === "") {
      return {
        status: "âŒ Your message cannot be an empty string.",
      };
    }

    //set up transaction parameters
    const transactionParameters = {
      to: contractAddress, // Required except during contract publications.
      from: address, // must match user's active address.
      data: dataMethod,
    };

    //sign the transaction
    try {
      const txHash = await window.ethereum.request({
        method: "eth_sendTransaction",
        params: [transactionParameters],
      });
      return {
        status: (
          <span>
            âœ…{" "}
            <a target="_blank" href={`https://goerli.etherscan.io/tx/${txHash}`}>
              View the status of your transaction on Etherscan!
            </a>
            <br />
            â„¹ï¸ Once the transaction is verified by the network, the message will
            be updated automatically.
          </span>
        ),
      };
    } catch (error) {
      return {
        status: "ğŸ˜¥ " + error.message,
      };
    }
  };
